$(document).ready(function ($) {
    $('.ranked').each(function () {
        var offer = $(this),
            getURL = offer.attr('data-url');
        $.get(getURL, function (data) {
            var textgiaban = $(data).find('.rank').text();
            offer.html('' + textgiaban + '')
        }, "html")
    })
});

